data:
{
  "lesson_topic": "市场营销英语",
  "sub_topics": [
    {
      "sub_topic": "介绍你的公司和产品",
      "learning_goals": [
        "用英语描述你的公司业务",
        "介绍公司的主要产品或服务",
        "讨论公司的市场定位"
      ]
    },
    {
      "sub_topic": "市场调研术语",
      "learning_goals": [
        "理解和使用常见的市场调研术语",
        "讨论目标市场和受众",
        "解读市场调研报告"
      ]
    },
    {
      "sub_topic": "广告和促销活动",
      "learning_goals": [
        "计划和讨论广告活动",
        "描述不同的促销策略",
        "评估广告效果"
      ]
    },
    {
      "sub_topic": "社交媒体营销",
      "learning_goals": [
        "讨论社交媒体平台的使用",
        "制定社交媒体内容策略",
        "分析社交媒体互动数据"
      ]
    },
    {
      "sub_topic": "客户关系管理 (CRM)",
      "learning_goals": [
        "讨论CRM系统的使用",
        "分析客户数据以改进营销策略",
        "处理客户反馈和投诉"
      ]
    },
    {
      "sub_topic": "撰写营销文案",
      "learning_goals": [
        "撰写吸引人的广告语",
        "撰写产品描述和新闻稿",
        "撰写电子邮件营销内容"
      ]
    },
    {
      "sub_topic": "参加行业会议和展览",
      "learning_goals": [
        "在会议中进行自我介绍",
        "与潜在客户建立联系",
        "展示公司产品和服务"
      ]
    },
    {
      "sub_topic": "谈判和达成协议",
      "learning_goals": [
        "与供应商谈判合同条款",
        "与合作伙伴达成合作协议",
        "解决营销活动中的问题"
      ]
    },
    {
      "sub_topic": "分析营销数据和报告",
      "learning_goals": [
        "理解营销KPI（关键绩效指标）",
        "分析数据以评估营销活动效果",
        "撰写营销报告"
      ]
    },
    {
      "sub_topic": "跨文化营销",
      "learning_goals": [
        "了解不同文化的营销差异",
        "调整营销策略以适应不同文化",
        "避免文化敏感问题"
      ]
    }
  ]
}
